import { CSSProperties } from "react";

const LargeButton = () => {
  //backgroundColor : 내가 좋아하는 색깔
  //padding: 10px 20px
  //borderRadius: 8px
  //버튼
  const buttonStyle: CSSProperties = {
    backgroundColor: "skyblue",
    padding: "10px, 20px",
    borderRadius: "8px",
  };

  const h1Style: CSSProperties = {
    color: "black",
    fontSize: "20px",
  };

  return (
    <>
      <button style={buttonStyle}>버튼입니다.</button>
      <h1 style={h1Style}>오늘 저녁 메뉴 라면</h1>
    </>
  );
};
//다른 곳에서 사용할 수 있도록
export default LargeButton;
